<template>
  <q-page ref="pageChat" class="page-chat flex column">
    <q-banner
      v-if="!otherUserDetails.online || otherUserDetails.invisible"
      class="bg-grey-4 text-center fixed-top"
    >
      {{ otherUserDetails.name }} is offline.
    </q-banner>
    <div
      :class="{ invisible: !showMessages }"
      class="q-pa-lg column col justify-end"
    >
      <q-chat-message
        ref="scrollMessages"
        v-for="(message, i) in groupedMessages"
        :class="!otherUserDetails.online ? 'q-mt-xl' : ''"
        :key="i"
        :sent="message[0].data.from == 'me' ? true : false"
        :bg-color="message[0].data.from == 'me' ? 'white' : 'light-green-2'"
        :size="message[0].size"
      >
        <template v-slot:name>
          {{
            message[0].data.from == "me"
              ? userDetails.name
              : otherUserDetails.name
          }}
        </template>
        <div class="text-body2" v-for="(msg, j) in message" :key="j">
          {{ msg.data.text }}
          <q-menu touch-position context-menu>
            <q-list dense style="min-width: 100px">
              <q-item
                clickable
                v-close-popup
                @click="removeMessage(msg.id, false)"
              >
                <q-item-section>Remove for You</q-item-section>
              </q-item>
              <q-separator v-if="msg.data.from == 'me'" />
              <q-item
                v-if="msg.data.from == 'me'"
                clickable
                v-close-popup
                @click="removeMessage(msg.id, true)"
              >
                <q-item-section>Remove for Everyone</q-item-section>
              </q-item>
            </q-list>
          </q-menu>
          <q-tooltip
            :anchor="msg.data.from == 'me' ? 'center left' : 'center right'"
            :self="msg.data.from == 'me' ? 'center right' : 'center left'"
            :offset="[12, 12]"
            :delay="550"
          >
            {{ relativeDate(msg.data.timestamp) }}
          </q-tooltip>
        </div>
      </q-chat-message>
    </div>
    <q-footer elevated>
      <q-toolbar>
        <q-form autofocus class="full-width">
          <q-input
            autogrow
            outlined
            rounded
            dense
            @keydown.enter.exact.prevent="sendMessage"
            v-model="newMessage.text"
            ref="newMessage"
            bg-color="white"
            label="Message"
          >
            <template v-slot:after>
              <q-btn
                round
                dense
                flat
                @click="sendMessage"
                type="submit"
                color="white"
                icon="send"
              />
            </template>
          </q-input>
        </q-form>
      </q-toolbar>
    </q-footer>
  </q-page>
</template>

<script>
import { defineComponent, ref } from "vue";
import { useQuasar } from "quasar";
import { mapState, mapActions, mapGetters } from "vuex";
import { mixinOtherUserDetails, mixinMethods } from "src/mixins";

export default defineComponent({
  name: "Chat App",

  mixins: [mixinOtherUserDetails, mixinMethods],

  data() {
    return {
      $q: useQuasar(),
      newMsg_NotifAudio: "notification.mp3",
      newMessage: {
        from: ref("me"),
        text: ref(""),
        timestamp: Date.now(),
        type: ref("text"),
      },
      showMessages: false,
      myMessages: [],
    };
  },

  computed: {
    ...mapState("firebase_auth", ["userDetails"]),
    ...mapGetters("firebase_chat", ["groupedMessages"]),
    ...mapState("firebase_chat", ["messages"]),
  },

  created() {
    if (!Object.keys(this.userDetails).length) this.$router.push("/");
    this.firebaseGetMessages({
      otherUserId: this.$route.params.otherUserId,
      audioSrc: this.newMsg_NotifAudio,
    });
  },

  watch: {
    messages: {
      handler(val) {
        if (Object.keys(val).length) {
          this.scrollToBottom();
          setTimeout(() => {
            this.showMessages = true;
          }, 200);
        }
      },
      deep: true,
    },
  },

  methods: {
    ...mapActions("firebase_chat", [
      "firebaseGetMessages",
      "firebaseStopGettingMessages",
      "firebaseSendMessage",
      "firebaseDeleteMessage",
    ]),
    sendMessage() {
      if (this.newMessage.text.length) {
        let message = {
          from: "me",
          text: this.newMessage.text.replace(/^\s+|\s+$/g, ""),
          timestamp: Date.now(),
          type: this.newMessage.type,
        };
        this.firebaseSendMessage({
          message: message,
          otherUserId: this.$route.params.otherUserId,
        });
        this.scrollToBottom();
        this.clearMessage();
      }
    },
    removeMessage(id, deleteForEveryone) {
      this.firebaseDeleteMessage({
        messageId: id,
        otherUserId: this.$route.params.otherUserId,
        deleteForEveryone: deleteForEveryone,
      });
    },
    clearMessage() {
      this.newMessage = {
        from: "me",
        text: "",
        timestamp: Date.now(),
        type: "text",
      };
      this.$refs.newMessage.focus();
    },
    scrollToBottom() {
      let pageChat = this.$refs.pageChat.$el;
      setTimeout(() => {
        window.scrollTo(0, pageChat.scrollHeight);
      }, 20);
    },
  },

  unmounted() {
    this.firebaseStopGettingMessages();
  },
});
</script>

